import{priorityList}from"@fusioncharts/core/src/schedular";function scrollTo(value){var chart=this.apiInstance;chart.addJob("scrollToAPoint",function(){if(value>=0&&value<=1){var children=chart.getChildren&&chart.getChildren(),xAxis=children.xAxis[0],scrollbar=xAxis.getChildren("scrollBar")&&xAxis.getChildren("scrollBar")[0],scrollAnchor=scrollbar&&scrollbar.getChildren("scrollAnchor")[0],limit=xAxis.getLimit&&xAxis.getLimit(),visibleConfig=xAxis.getVisibleConfig&&xAxis.getVisibleConfig(),visibleLength=visibleConfig.maxValue-visibleConfig.minValue,totalLength=limit.max-limit.min,start=value*(totalLength-visibleLength)+limit.min,end=start+visibleLength,previousValue=(visibleConfig.minValue-limit.min)/(totalLength-visibleLength);scrollAnchor.config.scrollPosition=value;chart.fireChartInstanceEvent("scrollStart",{scrollPosition:previousValue});xAxis.setVisibleConfig(start,end);chart.fireChartInstanceEvent("scrollEnd",{previousScrollPosition:previousValue,scrollPosition:value})}},priorityList.postRender)}export{scrollTo};