import FusionCharts from"../../../../../fusioncharts/core/index";import{TimeSeries}from"../../../index";import DataStore from"../../../../../fc-datatable";import{setup,CONTAINER_ID}from"../../../../../../dev-tests/viz/utitilies";FusionCharts.addDep(TimeSeries);var data=[["2016-10-30 09:00:00","2018-10-30 09:00:00",1,"A",2,13],["2016-10-30 09:30:00","2018-10-30 09:30:00",20,"A",3,49],["2016-10-30 10:00:00","2018-10-30 10:00:00",3,"B",4,25],["2016-10-30 10:30:00","2018-10-30 10:30:00",14,"B",5,60],["2016-10-30 11:00:00","2018-10-30 11:00:00",50,"C",6,17],["2016-10-30 11:30:00","2018-10-30 11:30:00",26,"C",7,80]],schema=[{name:"Time",type:"date",format:"%Y-%m-%d %H:%M:%S"},{name:"Time-2",type:"date",format:"%Y-%m-%d %H:%M:%S"},{name:"Measure1",type:"number"},{name:"Item",type:"string"},{name:"Measure2",type:"number"},{name:"Measure3",type:"number"}];var fusionDataStore=new DataStore,fusionTable=fusionDataStore.createDataTable(data,schema);describe("the label of navigator window",function(){var chartData={type:"timeseries",renderAt:CONTAINER_ID,width:600,height:400,dataSource:{chart:{},data:fusionTable,yAxis:[{plot:{value:"Measure1",type:"line"}}],navigator:{window:{style:{label:{"font-size":"30px",fill:"#ff0000"},"label-background":{fill:"#0000ff"}}}}}},chart;beforeEach(function(){chart=setup(FusionCharts,chartData)});afterEach(function(){return chart.dispose()});it("can be configured",function(done){chart.addEventListener("renderComplete",function(e){e.detachHandler();var brushGroup=document.querySelector('[class$="-brush-group"]');brushGroup.dispatchEvent(new MouseEvent("mouseover",{bubbles:true}));setTimeout(function(){var rects=brushGroup.getElementsByTagName("rect"),texts=brushGroup.getElementsByTagName("text");expect(getComputedStyle(rects[3]).fill).toBe("rgb(0, 0, 255)");expect(getComputedStyle(rects[4]).fill).toBe("rgb(0, 0, 255)");expect(getComputedStyle(texts[0]).fill).toBe("rgb(255, 0, 0)");expect(getComputedStyle(texts[1]).fill).toBe("rgb(255, 0, 0)");expect(getComputedStyle(texts[0]).fontSize).toBe("30px");expect(getComputedStyle(texts[1]).fontSize).toBe("30px");done()},32)});chart.render()})});