import FlagMarker from"./flag-marker";describe("To verify _checkPointOverMarker method",function(){var flagMarker=new FlagMarker;flagMarker.config.dataInfo=[{x:0,y:100},{x:40,y:100},{x:80,y:120},{x:120,y:120}];flagMarker.config.markerInfo=[{},{},{},{}];it("FM-TC-11: To verify if the method returns correct n'th marker index for specific case of pointer x,y inside the marker",function(){var hovered=flagMarker._checkPointOverMarker(1,40,94);expect(hovered&&hovered.pointIndex).toBe(1)});it("FM-TC-12: To verify if the method returns n'th plot when pointer x, y is on the edge of the plot",function(){var point=flagMarker._checkPointOverMarker(1,47,100);expect(point&&point.pointIndex).toBe(1);point=flagMarker._checkPointOverMarker(1,48,72);expect(point&&point.pointIndex).toBe(1);point=flagMarker._checkPointOverMarker(1,40,72);expect(point&&point.pointIndex).toBe(1)});it("FM-TC-13: To verify if the method does not return n'th plot when pointer x, y is out of the plot",function(){var point=flagMarker._checkPointOverMarker(0,9,110);expect(point&&point.pointIndex).not.toBe(0);point=flagMarker._checkPointOverMarker(0,9,129);expect(point&&point.pointIndex).not.toBe(0)})});