import FlagMarker from"./pin-marker";describe("To verify _checkPointOverMarker method",function(){var flagMarker=new FlagMarker;flagMarker.config.dataInfo=[{x:0,y:100},{x:40,y:100},{x:80,y:120},{x:120,y:120}];flagMarker.config.markerInfo=[{},{},{},{}];it("PM-TC-08: To verify if the method returns correct n'th marker index for specific case of pointer x,y inside the marker",function(){var hovered=flagMarker._checkPointOverMarker(1,40,94);expect(hovered&&hovered.pointIndex).toBe(1)});it("PM-TC-09: To verify if the method returns n'th plot when pointer x, y is on the edge of the plot",function(){var point=flagMarker._checkPointOverMarker(1,44.5,100);expect(point&&point.pointIndex).toBe(1)});it("PM-TC-10: To verify if the method does not return n'th plot when pointer x, y is out of the plot",function(){var point=flagMarker._checkPointOverMarker(0,9,110);expect(point&&point.pointIndex).not.toBe(0);point=flagMarker._checkPointOverMarker(0,9,129);expect(point&&point.pointIndex).not.toBe(0);point=flagMarker._checkPointOverMarker(0,8,94);expect(point&&point.pointIndex).not.toBe(0);point=flagMarker._checkPointOverMarker(0,-8,94);expect(point&&point.pointIndex).not.toBe(0)})});