import precisionPrefix from"./precision-prefix";describe("precisionPrefix",function(){it("should return zero if step has the same units as value",function(){for(var i=-24;i<=24;i+=3){for(var j=i;j<i+3;++j){expect(precisionPrefix(+("1e"+i),+("1e"+j))).toBe(0)}}});it("should return greater than zero if fractional digits are needed",function(){for(var i=-24;i<=24;i+=3){for(var j=i-4;j<i;++j){expect(precisionPrefix(+("1e"+j),+("1e"+i))).toBe(i-j)}}});it("should return the expected precision when value is less than one yocto",function(){expect(precisionPrefix(1e-24,1e-24)).toBe(0);expect(precisionPrefix(1e-25,1e-25)).toBe(1);expect(precisionPrefix(1e-26,1e-26)).toBe(2);expect(precisionPrefix(1e-27,1e-27)).toBe(3);expect(precisionPrefix(1e-28,1e-28)).toBe(4)});it("should return the expected precision when value is greater than than one yotta",function(){expect(precisionPrefix(1e24,1e24)).toBe(0);expect(precisionPrefix(1e24,1e25)).toBe(0);expect(precisionPrefix(1e24,1e26)).toBe(0);expect(precisionPrefix(1e24,1e27)).toBe(0);expect(precisionPrefix(1e23,1e27)).toBe(1)})});