import inDelta from"./in-delta";import interpolateNumber from"./number";describe("interpolateNumber(a, b) should",function(){it("interpolate between a and b",function(){var interpolator=interpolateNumber(10,42);expect(inDelta(interpolator(0),10)).toBeTruthy();expect(inDelta(interpolator(.1),13.2)).toBeTruthy();expect(inDelta(interpolator(.2),16.4)).toBeTruthy();expect(inDelta(interpolator(.3),19.6)).toBeTruthy();expect(inDelta(interpolator(.4),22.8)).toBeTruthy();expect(inDelta(interpolator(.5),26)).toBeTruthy();expect(inDelta(interpolator(.6),29.2)).toBeTruthy();expect(inDelta(interpolator(.7),32.4)).toBeTruthy();expect(inDelta(interpolator(.8),35.6)).toBeTruthy();expect(inDelta(interpolator(.9),38.8)).toBeTruthy();expect(inDelta(interpolator(1),42)).toBeTruthy()})});