import TimeInterval from"./time-interval.js";import{durationHour,durationMinute}from"./durations.js";var hour=new TimeInterval("hour",function(d){var offset=d.getTimezoneOffset()*durationMinute%durationHour;if(offset<0)offset+=durationHour;d.setTime(Math.floor((Number(d)-offset)/durationHour)*durationHour+offset)},function(d,s){return d.setTime(Number(d)+s*durationHour)},function(s,e){return(e-s)/durationHour},function(d){return d.getHours()});export default hour;